struct pollfd fds[2]; // Monitor sock1 for input
fds[0].fd = sock1;
fds[0].events = POLLIN; // Monitor sock2 for output
fds[1].fd = sock2;
fds[1].events = POLLOUT; // Wait 10 seconds
